var d = new Date();
var dst = d.getTimezoneOffset(); // DST day light savign time
//var hr = d.getHours()+dst;
var hr = d.getHours();
var _tm = "d:"+d.getDay()+" h:"+hr+" m:"+ d.getMinutes()+" s:"+ d.getSeconds();
msg.payload = null; 

function getEvents(){

	//_log("scheduler run at:"+_tm+" hr:"+hr+" dst:"+dst);

	for (var item in schedule) {
		var shdId =  item.split('-');
		var itemI = parseInt(shdId[1],10);  // Convert shdId to integer e.g. Shd-1 to 1
		var event =  schedule[item];
		//node.log("sid:"+item+" dow:"+event.dofWeek[d.getDay()]+" state:"+ scheduledState[itemI]+" type:"+ typeof  itemI);

		if(event.disabled === false){
    		// Check for scheduled Start event		
    		var hrmin=event.startTime.split(":");
    		//_log(item+" enabled - scheduled today?: "+(event.dofWeek[d.getDay()] === 0 ? 'No' : 'Yes')+" - run state: "+ scheduledState[itemI]);
    		_log(item+' shd-hr: '+parseInt(hrmin[0])+' hr: '+hr+' shd-min: '+parseInt(hrmin[1])+' min: '+d.getMinutes());  

    		if(parseInt(hrmin[0]) === hr &&  parseInt(hrmin[1]) === d.getMinutes() &&  scheduledState[itemI]=== null &&  event.dofWeek[d.getDay()] === 1){
    			msg.payload = event.startValue;
    			msg.item = item;
    			msg.msgType =  'shdEvent';
    			msg.topic = item+': '+event.tag+' &#13;&#10;Schedule Start ran at: '+hrmin[0]+":"+hrmin[1]+'&#13;&#10;';
    			scheduledState[itemI]=1; // run Start shed settings
    			flow.set('scheduledState',scheduledState,'file'); 
    			_log("sid:"+item+" started v: "+ msg.payload+"  tag: "+ event.tag +" state:"+scheduledState[itemI]);
                node.send([msg,null]);
    		}
    		// Check for scheduled End event
    		hrmin=event.endTime.split(":");
    		if(parseInt(hrmin[0]) === hr &&  parseInt(hrmin[1]) === d.getMinutes() &&  scheduledState[itemI] === 1 &&  event.dofWeek[d.getDay()] === 1){
    			msg.payload = event.endValue;
    			msg.item = item;
    			msg.msgType =  'shdEvent';
    			msg.topic = item+': '+event.tag+' &#13;&#10;Schedule End ran at: '+hrmin[0]+":"+hrmin[1]+'&#13;&#10;';
    			scheduledState[itemI] = 2; // run End shed settings
    			flow.set('scheduledState',scheduledState,'file'); 
    			_log("sid:"+item+" ended v: "+ msg.payload+"  tag: "+ event.tag +" state:"+scheduledState[itemI]);
                node.send([msg,null]);
    		}
		} else {
		    //_log(item+': disabled');
		}  
	}
}

function scheduleReset(){
    node.log(msg.topic);
	if(dayOfWeek != d.getDay() || msg.topic === 'reset'){

		if(dayOfWeek === 'undefined'){
			_log( "reset schedule due to node-red restart");
			dayOfWeek = d.getDay();
			flow.set('dayOfWeek',d.getDay());
		} 
		if (dayOfWeek != d.getDay()){
			_log( "reset schedule due to change of day:"+d.getDay());	
		    flow.set('dayOfWeek',d.getDay());
		}
		if (msg.topic === 'reset'){
			_log('reset schedule');		
		}

		for (var item in schedule) {
			var shdId = item.split('-');
            var idx = parseInt(shdId[1],10);
			//_log("schedule item:"+item+" idx:"+idx);
			if(scheduledState[idx] !== null){
				scheduledState[idx]=null;
			    flow.set('scheduledState',scheduledState,'file'); 
				//_log("schedule item:"+item+" manualy reset");
			}
		}
	}
}
function _log(item){
    var _log = {payload: item};
    node.send([null,_log]);
}
function dayName(dayNo){
    var dow = ['Sun','Mon','Tues','Wed','Thu','Fri','Sat'];
    return dow[dayNo];
}

